mapper.select:
select b.no as no, b.title as title, b.contents as contents, b.hit as hit, date_format(b.reg_date, '%Y-%m-%d %h:%i:%s') as regDate, b.g_no as gNo, b.o_no as oNo, b.depth as depth, u.no as userNo, u.name as userName, b.status as status from board b join user u on b.user_no = u.no
where title like CONCAT('%', #{kwd}, '%') or contents like CONCAT('%', #{kwd}, '%')
order by g_no desc, o_no asc, reg_date desc limit #{startIndex}, #{pageSize}


update board 
   set hit = hit+1 
 where no = #{boardNo}

mapper.update:

update board 
   set o_no = o_no+1 
 where g_no = #{gNo}
   and o_no >= #{oNo}
